```
package main

import (
    "fmt"
    "time"
)

func main() {
    ch := make(chan int)

    go func(chan int) {
        var count int
        for {
            count++
            ch <- count
            time.Sleep(10 * time.Second)
        }
    }(ch)

    for v := range ch {
        fmt.Println(v)
    }
} 
